mapper.select:
select mt.*, mm.member_name, mm.member_profile, mm.member_email, mt.mentor_seq,j.job_type
from mentor mt, mentors_member mm, job j
where mt.mentor_email = mm.member_email and j.job_code = mt.job_code and mm.member_seq = ${mentor_seq}

select count(*) from(select mt.* , mb.member_name , mb.member_seq , mb.member_profile , j.job_type from mentor mt, job j, mentors_member mb where mt.mentor_email = mb.member_email and j.job_code = mt.job_code and mentor_flag = #{mentor_flag}
order by mt.mentor_logtime desc
and mt.mentor_badge > 0
) where job_code in
#{job_code, jdbcType=VARCHAR}

		select * from(select rownum rn, tt.* from(select mt.*
												, mb.member_name
					                            , mb.member_seq
					                            , mb.member_profile
					                            , j.job_type
		from mentor mt, job j, mentors_member mb 
           where mt.mentor_email = mb.member_email and j.job_code = mt.job_code and mentor_flag = #{mentor_flag}

              	ORDER BY mt.mentor_logtime DESC


           		AND mt.mentor_badge > 0 

           )tt
           where job_code in

        	  #{job_code, jdbcType=VARCHAR}

         )

              where rn >= #{startNum, jdbcType=INTEGER} and rn<= #{endNum, jdbcType=INTEGER}

mapper.update:
update mentor set mentor_company = #{mentor_company}
				, mentor_department = #{mentor_department}
				, mentor_position = #{mentor_position}
				, job_code = #{job_code}
				, mentor_school = #{mentor_school}
				, mentor_career = #{mentor_career}
				, mentoring_code = #{mentoring_code}
				, mentor_represent = #{mentor_represent}
				, mentor_info = #{mentor_info}
				, mentor_etc = #{mentor_etc}
				, mentor_selectnaming = #{mentor_selectNaming}

				, mentor_businesscard = #{mentor_businesscard}

				where mentor_email = #{mentor_email}
