mapper.insert:
INSERT
	into essayboard
	(
		essayboard_seq,
		mentor_email,
		job_code,
		essayboard_title,
		essayboard_content,
		essayboard_logtime
	)
VALUES(essayboard_seq.nextval
	, #{mentor_email, jdbcType=VARCHAR}
	, #{job_code, jdbcType=VARCHAR}
	, #{essayboard_title, jdbcType=VARCHAR}
	, #{essayboard_content, jdbcType=VARCHAR}
	, sysdate
)

mapper.update:
UPDATE
	essayboard
SET
  	essayboard_title = #{essayboard_title, jdbcType=VARCHAR}
  , essayboard_content = #{essayboard_content, jdbcType=VARCHAR}
  , job_code = #{job_code, jdbcType=VARCHAR}
WHERE
 essayboard_seq = #{seq, jdbcType=INTEGER}

mapper.select:
select * from (select rownum rn, tt.* from (SELECT e.*, j.job_type, mm.member_name, mm.member_seq, mm.member_profile, mt.mentor_company, mt.mentor_department, mt.mentor_represent, mt.mentor_flag FROM essayboard e, job j, mentor mt, mentors_member mm WHERE e.job_code = j.job_code AND e.mentor_email = mt.mentor_email AND mt.mentor_email = mm.member_email
ORDER BY e.essayboard_logtime DESC
AND e.essayboard_scrap > 0
)tt WHERE job_code IN
#{job_code, jdbcType=VARCHAR}
)
where rn >= #{startNum, jdbcType=INTEGER} and rn<= #{endNum, jdbcType=INTEGER}

SELECT COUNT(*) FROM (SELECT e.*, j.job_type, mm.member_name, mm.member_seq, mt.mentor_company, mt.mentor_department, mt.mentor_represent, mtr.mentoring_code, mtr.mentoring_type FROM essayboard e, job j, mentor mt, mentoring mtr, mentors_member mm WHERE e.job_code = j.job_code AND e.mentor_email = mt.mentor_email AND mt.mentor_email = mm.member_email AND mt.mentoring_code = mtr.mentoring_code
ORDER BY e.essayboard_logtime DESC
AND e.essayboard_scrap > 0
) WHERE job_code IN
#{job_code, jdbcType=VARCHAR}

SELECT * FROM (SELECT ROWNUM rn, tt.* FROM (SELECT e.essayboard_seq, e.essayboard_title, e.essayboard_content, e.essayboard_scrap, e.essayboard_logtime, mm.member_name, mm.member_seq, mm.member_profile, mt.mentor_company, mt.mentor_department, mt.mentor_flag, mt.mentor_email FROM essayboard e, mentor mt, mentors_member mm WHERE e.mentor_email = mt.mentor_email AND mt.mentor_email = mm.member_email AND e.essayboard_scrap > 0 ) tt) WHERE
rn >= #{startNum} and rn <= #{endNum}
order by essayboard_logtime desc
